
/**
 * @description 是在网页上执行的javascript，可以读取和修改网页上的dom元素，但只能使用部分的浏览器api
 */
const BASE64 = 'data:image/gif;base64,';
const DATABASENAME = 'DataBase';

let popBtn = '', selectionTimeout = null;

const addToStore = async (text) => {
    const storage = await chrome.storage.sync.get([DATABASENAME]);
    const dataBase = storage[DATABASENAME];
    dataBase.push({
        id: dataBase.length,
        text: text,
        time: new Date().getTime()
    });
    await chrome.storage.sync.set({ [DATABASENAME]: dataBase });
}

const updateBadge = async () => {
    await chrome.runtime.sendMessage({ type: 'setBadgeText' });
}

document.addEventListener('selectionchange', (e) => {
    clearTimeout(selectionTimeout);
    selectionTimeout = setTimeout(() => {
        let text = '';
        const selection = window.getSelection();
        try {
            const range = selection?.getRangeAt?.(0);
            text = range?.toString?.(); // 使用这种方式获取实际选中的文本
        } catch (error) {
            text = '';
        }
        if (selection?.rangeCount > 0 && text.length > 2 && !popBtn) {
            const range = selection.getRangeAt(0);
            const rect = range.getBoundingClientRect();
            popBtn = document.createElement('span');
            popBtn.className = 'action-container__action';
            popBtn.id = 'popadd';
            popBtn.innerHTML = `<img class="svg-icon-popbtn" src="${BASE64}" alt="文案助手" title="添加到文案助手">`;
            // popBtn.textContent = '操作按钮';
            popBtn.style.position = 'fixed';
            popBtn.style.left = rect.left + 'px';
            popBtn.style.top = rect.top - 40 + 'px';
            popBtn.style.zIndex = 10000;
            document.body.appendChild(popBtn);
            // 可以在这里添加按钮的点击事件处理等逻辑
            popBtn.onclick = (e) => {
                e.stopPropagation();
                addToStore(text);
                updateBadge();
                document.body.removeChild(popBtn);
                popBtn = null;
            }
        }
    }, 500); // 适当延迟以确保选择完成
}, false);


document.addEventListener('click', (e) => {
    if (popBtn) {
        document.body.removeChild(popBtn);
        popBtn = null;
    }
}, false);